import React from 'react';
import ReactDOM from 'react-dom';

class Joke extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.joke={
            setup:"",
            punchline:""
        };

    }

    handleClick() {
        this.getJoke();
    }

    getJoke() {
        fetch("https://official-joke-api.appspot.com/random_joke")
            .then(response => response.json())
            .then(data => {
                const obj = data;
                this.joke.setup=obj["setup"];
                this.joke.punchline = obj["punchline"];
                console.log(`setup: ${this.joke.setup}
                            punchline: ${this.joke.punchline}`)                             
            })
            .catch(() => {
                console.log("Error!");
            });
    }



    render() {
        return (
            <div>
                <h1>Get a joke!</h1>

                <button id="ok" onClick={this.handleClick}>GET A RANDOM JOKE</button>

                <div>

                </div>

            </div>
        );
    }
}



ReactDOM.render(
    <Joke />,
    document.getElementById('root')
);
