import React from 'react';
import ReactDOM from 'react-dom';

class Joke extends React.Component {
    constructor(props) {
        super(props);
        this.updateInfo = this.updateInfo.bind(this);
        this.showPunchline = this.showPunchline.bind(this);
        this.toggleOn = this.toggleOn.bind(this);
        this.addToFavorite = this.addToFavorite.bind(this);
        this.showFavorite = this.showFavorite.bind(this);
        this.switchisFavoriteToggleOn = this.switchisFavoriteToggleOn.bind(this);

        this.state = {
            setup: "",
            punchline: "",
            isToggleOn: true,
            isFavoriteToggleOn: false
        };

    }

    updateInfo() {
        this.getJoke();
        this.toggleOn(false);
        this.switchisFavoriteToggleOn(false);
    }

    showPunchline() {
        return (
            <div>
                {this.state.punchline}
            </div>
        )
    }

    toggleOn(boolCondition = false) {
        this.setState(state => ({
            isToggleOn: boolCondition
        }));
    }

    getJoke() {
        fetch("https://official-joke-api.appspot.com/random_joke")
            .then(response => response.json())
            .then(data => {
                const obj = data;
                this.setState({ setup: obj["setup"], punchline: obj["punchline"] });

                console.log(`setup: ${this.state.setup}      punchline: ${this.state.punchline}`);
            })
            .catch(() => {
                console.log("Error!");
            });
    }

    addToFavorite() {
        localStorage.setItem(this.state.setup, this.state.punchline);
    }

    showFavorite() {
        let string = "";
        for (let [key, value] of localStorage) {
            string += `${key}<br></br>${value}<br></br><br></br>`;
        }
        return string;
    }

    switchisFavoriteToggleOn(boolCondition = true) {
        this.setState(state => ({
            isFavoriteToggleOn: boolCondition
        }));
    }


    render() {
        return (
            <div>
                <h1>Get a joke!</h1>

                <button id="nextJoke" onClick={this.updateInfo}>GET A RANDOM JOKE</button>

                <button id="showFavorite" onClick={this.switchisFavoriteToggleOn}>SHOW FAVORITE JOKES</button>

                {(this.state.setup.length) ? (<button id="showPunch" onClick={this.toggleOn}>SHOW PUNCHLINE</button>) : <div></div>}

                {(this.state.setup.length) ? (<button id="addToFavorite" onClick={this.addToFavorite}>ADD TO FAVORITEE</button>) : <div></div>}

                {(this.state.isFavoriteToggleOn) ?
                    (
                        <div>
                            {this.state.setup}
                            <br></br>
                            {this.state.isToggleOn ? this.showPunchline() : ' '}
                        </div>
                    ) :
                    (<div>{this.showFavorite}</div>)
                }

            </div>
        );
    }
}



ReactDOM.render(
    <Joke />,
    document.getElementById('root')
);
