import React from 'react';
import ReactDOM from 'react-dom';

class Joke extends React.Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            setup: "",
            punchline: ""
        };

    }

    handleClick() {
        this.getJoke();
    }

    showPunchline(){
        return this.state.punchline;
    }

    getJoke() {
        fetch("https://official-joke-api.appspot.com/random_joke")
            .then(response => response.json())
            .then(data => {
                const obj = data;
                this.setState({ setup: obj["setup"], punchline: obj["punchline"] });

                console.log(`setup: ${this.state.setup}      punchline: ${this.state.punchline}`);
            })
            .catch(() => {
                console.log("Error!");
            });
    }


    render() {
        return (
            <div>
                <h1>Get a joke!</h1>

                <button id="ok" onClick={this.handleClick}>GET A RANDOM JOKE</button>
                {(this.state.setup.length) ? (<button id="ok" onClick={this.showPunchline}>SHOW PUNCHLINE</button>) : <div></div> }
                <div>
                    {this.state.setup}
                    <br></br>

                </div>

            </div>
        );
    }
}



ReactDOM.render(
    <Joke />,
    document.getElementById('root')
);
